package Whilecond;

public class PrintprimenumbersDowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//WAP to print numbers b/w 15 to 20 using do while
		
		
		   int i=15;
				   
		    do
		    {
		    	if (i%2==0)
		    		
		    	{
		    		
		    		System.out.println(i);
		    			
		    	}i++;
		    	
		    }while(i<=20);

	}

}
